<?php\nrequire 'vendor/autoload.php';\n\n// Create a new instance of the Identicon class\nuse Jdenticon\Identicon;\n\n$hash = md5('example@example.com'); // Any hash will work\n$identicon = new Identicon($hash);\n\n// Save as PNG\n$imageData = $identicon->getImageData(64);\nfile_put_contents('identicon.png', $imageData);\n\n// Or output directly to the browser\nheader("Content-type: image/png");\necho $imageData;\n?>