require_once 'vendor/autoload.php';\n\nfunction getGoogleContacts($accessToken) {\n    $client = new Google_Client();\n    $client->setAccessToken($accessToken);\n\n    $service = new Google_Service_PeopleService($client);\n    $results = $service->people_connections->listPeopleConnections('people/me', ['personFields' => 'names,emailAddresses']);\n\n    if (count($results->getConnections()) == 0) {\n        print "No contacts found.\n";\n    } else {\n        foreach ($results->getConnections() as $person) {\n            if (!empty($person->getEmailAddresses())) {\n                $email = $person->getEmailAddresses()[0]->getValue();\n                echo "Email: $email\n";\n            }\n        }\n    }\n}\n\n// Example call\n$accessToken = 'your_access_token_here';\ngetGoogleContacts($accessToken);