// Using PDO for database connection\ntry {\n    $pdo = new PDO('mysql:host=hostname;dbname=databasename;charset=utf8mb4', 'username', 'password');\n    // Set the PDO error mode to exception\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n} catch (PDOException $e) {\n    echo "Connection failed: " . $e->getMessage();\n}\n// No need to explicitly close the connection when using PDO in PHP. It will close automatically at script end.