function splitQueries($sql) {\n    $queries = [];\n    $queryBuffer = '';\n    $delimiter = ';';\n    $insideString = false;\n    $escaped = false;\n\n    for ($i = 0; $i < strlen($sql); $i++) {\n        $char = $sql[$i];\n\n        if ($insideString) {\n            // Check for end of string\n            if ($char === "'" && !$escaped) {\n                $insideString = false;\n            }\n        } else {\n            // Check for start of string\n            if ($char === "'") {\n                $insideString = true;\n            }\n\n            // Detect if the semicolon is not escaped and outside of quotes, to split query\n            if ($char === ';' && !$escaped) {\n                $queries[] = trim($queryBuffer . ';');\n                $queryBuffer = '';\n                continue;\n            }\n        }\n\n        // Check for escape character (backslash)\n        if ($char === '\\') {\n            $escaped = !$escaped;\n        } else {\n            $escaped = false;\n        }\n\n        $queryBuffer .= $char;\n    }\n\n    // Handle any leftover query in the buffer\n    if (!empty($queryBuffer)) {\n        $queries[] = trim($queryBuffer);\n    }\n\n    return $queries;\n}\n\n// Usage:\n$content = file_get_contents('path_to_your_sql_dump.sql');\n$queries = splitQueries($content);\n\nforeach ($queries as $query) {\n    if (!empty($query)) { // Optional: avoid executing empty queries\n        // Execute the query using your MySQL class\n        // $yourMySQLClassInstance->executeQuery($query);\n    }\n}