$backupFile = $table . "-" . date("Y-m-d-H:i:s") . '.gz';\n// Set the MySQL password in an environment variable for better security\nputenv("MYSQL_PWD=$dbpass");\n$command = "mysqldump --opt -h $dbhost -u $dbuser $dbname | gzip > /path/to/your/backups/$backupFile";\n$status = passthru($command, $a);\necho "output:" .  $a;\n\nif ($a === 0) {\n    echo "Backup successful: $backupFile<br>";\n} else {\n    echo "An error occurred during backup. Return code: " . $a . "<br>";\n}