<?php\n\nfunction getNextTempFileName($directory = '.', $prefix = 'tmp_file_', $extension = '.jpg') {\n    $counter = 1;\n    do {\n        // Format the number as two digits with leading zero if necessary\n        $numberedFile = sprintf('%s%02d%s', $prefix, $counter, $extension);\n        $fullPath = $directory . DIRECTORY_SEPARATOR . $numberedFile;\n        $counter++;\n    } while (file_exists($fullPath));\n\n    return $fullPath;\n}\n\n// Usage example:\n$tempFileName = getNextTempFileName();\necho "Next available file name is: " . $tempFileName;\n\n?>