function getAll($id) {\n    // Specify the fields you want to select explicitly\n    $this->db->select('movies.title, movies.year, movies.runtime, movies.plotoutline, posters.poster_url');\n    $this->db->from('movies');\n    $this->db->join('posters', 'movies.id = posters.id'); // Ensure the join condition is correct\n    $this->db->where('movies.id', $id); // Apply the WHERE clause to filter by the provided ID\n    $q = $this->db->get();\n\n    if ($q->num_rows() == 1) {\n        $row = $q->row(); // Fetch the row as an object\n        // Prepare the data array without including the primary key id twice\n        $data = array(\n            'title' => $row->title,\n            'year' => $row->year,\n            'runtime' => $row->runtime,\n            'plotoutline' => $row->plotoutline,\n            'poster_url' => $row->poster_url\n        );\n    }\n\n    $q->free_result(); // Free the result set\n    return isset($data) ? $data : array(); // Return the data or an empty array if no row was found\n}