<?php\n$pageno = filter_input(INPUT_GET, 'pageno', FILTER_SANITIZE_NUMBER_INT);\nif (!is_numeric($pageno) || $pageno <= 0) {\n    echo "Invalid page number";\n    exit();\n}\n// Example to fetch comments from a database (ensure you use prepared statements)\n$comments = getCommentsFromDatabase($pageno); // Your function to get comments\necho json_encode(['comments' => $comments]);\n?>