// On user login, fetch necessary IDs and store in session\nfunction loginUser($userId) {\n    global $conn; // Assuming MySQL connection is stored in $conn\n\n    // Fetch saved searches\n    $savedSearchesQuery = "SELECT search_id FROM saved_searches WHERE user_id = ?";\n    $stmt = $conn->prepare($savedSearchesQuery);\n    $stmt->bind_param("i", $userId);\n    $stmt->execute();\n    $result = $stmt->get_result();\n    $savedSearches = [];\n    while ($row = $result->fetch_assoc()) {\n        $savedSearches[] = $row['search_id'];\n    }\n    \n    // Fetch lightboxes\n    $lightboxesQuery = "SELECT lb_id FROM lightboxes WHERE user_id = ?";\n    $stmt = $conn->prepare($lightboxesQuery);\n    $stmt->bind_param("i", $userId);\n    $stmt->execute();\n    $result = $stmt->get_result();\n    $lightboxes = [];\n    while ($row = $result->fetch_assoc()) {\n        $lightboxes[] = $row['lb_id'];\n    }\n    \n    // Fetch user groups\n    $userGroupsQuery = "SELECT group_id FROM user_groups WHERE user_id = ?";\n    $stmt = $conn->prepare($userGroupsQuery);\n    $stmt->bind_param("i", $userId);\n    $stmt->execute();\n    $result = $stmt->get_result();\n    $userGroups = [];\n    while ($row = $result->fetch_assoc()) {\n        $userGroups[] = $row['group_id'];\n    }\n\n    $_SESSION['saved_searches'] = $savedSearches;\n    $_SESSION['lightboxes'] = $lightboxes;\n    $_SESSION['user_groups'] = $userGroups;\n\n    // Continue with login process...\n}