<?php\n   $servername = "your_server";\n   $username = "your_username";\n   $password = "your_password";\n   $dbname = "your_dbname";\n\n   // Create connection\n   $conn = new mysqli($servername, $username, $password, $dbname);\n\n   // Check connection\n   if ($conn->connect_error) {\n       die("Connection failed: " . $conn->connect_error);\n   }\n\n   $sql = "SELECT last_updated FROM your_table_name WHERE id=your_id"; // Adjust query as needed to get the correct row\n   $result = $conn->query($sql);\n\n   if ($result->num_rows > 0) {\n       $row = $result->fetch_assoc();\n       $lastUpdated = new DateTime($row['last_updated']);\n       $currentDateTime = new DateTime();\n       \n       // Calculate difference in minutes\n       $interval = $currentDateTime->diff($lastUpdated);\n       $minutesDiff = $interval->i + ($interval->h * 60) + ($interval->days * 24 * 60);\n\n       if ($minutesDiff > 30) {\n           echo "The database was updated more than 30 minutes ago.";\n       } else {\n           $minutesLeft = 30 - $minutesDiff;\n           echo "The database will be updatable in another $minutesLeft minute(s).";\n       }\n   } else {\n       echo "No records found.";\n   }\n\n   $conn->close();\n   ?>