function incrementHitSQLite($dbname, $pageName) {\n    // Create connection and open database\n    try {\n        $pdo = new PDO("sqlite:$dbname");\n        $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n        \n        // Prepare the statement for inserting or updating hits\n        $stmt = $pdo->prepare("\n            INSERT INTO page_hits (PageName, Hits) VALUES (:pageName, 1)\n            ON CONFLICT(PageName) DO UPDATE SET Hits = Hits + 1;\n        ");\n        $stmt->execute([':pageName' => $pageName]);\n\n    } catch (PDOException $e) {\n        throw new Exception("SQLite error: " . $e->getMessage());\n    } finally {\n        // Close the connection\n        $pdo = null;\n    }\n\n    return true;\n}