class User {\n    private $id;\n    private $email;\n    private $username;\n\n    function __construct($id = false, $email = false, $username = false) {\n        if (!$id && !$email && !$username) {\n            throw new InvalidArgumentException("At least one of id, email, or username must be provided.");\n        }\n        \n        $this->id = $id;\n        $this->email = $email;\n        $this->username = $username;\n    }\n}\n\ntry {\n    $u1 = new User(); // This will throw an exception\n} catch (InvalidArgumentException $e) {\n    echo "Error: " . $e->getMessage();\n}