function checkDomainAvailability($domain) {\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, "http://www.whoisapi.de/api/whois/?format=json&output=shortdomainstatus&domain=" . urlencode($domain));\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    $response = curl_exec($ch);\n    curl_close($ch);\n\n    $data = json_decode($response, true);\n\n    if ($data && isset($data['results']) && isset($data['results']['0'])) {\n        return ($data['results']['0'] == 'free') ? 'available' : 'unavailable';\n    }\n    \n    return 'unknown';\n}\n\n$domainToCheck = "example.com";\n$status = checkDomainAvailability($domainToCheck);\necho "The domain $domainToCheck is $status.";