<?php\n$dir = '/path/to/your/folder1/'; // Use server path not URL\n\n// Get all .jpg, .jpeg files in the directory with full path\n$images_array = glob($dir . '*.{JPG,jpg,jpeg,JPEG}', GLOB_BRACE);\n\nforeach ($images_array as $v) {\n    $image_url = str_replace('/path/to/your/', 'http://www.example.com/', $v); // Convert server path to URL\n    echo '<img src="' . htmlspecialchars($image_url) . '" />';\n}\n?>