class SubClass extends SuperClass {\n  public function __construct() {\n    parent::__construct(__FILE__);\n  }\n}\n\nclass SuperClass {\n  private $subclassFile;\n\n  public function __construct($subclassFile) {\n    $this->subclassFile = $subclassFile;\n    // this echoes "I'm SubClass and I'm extending SuperClass, defined in subclass.php"\n    echo 'I\'m '.get_class($this).' and I\'m extending '.__CLASS__.", defined in ".basename($this->subclassFile);\n  }\n}\n\n// Example usage\n$sub = new SubClass();