<?php\n\nrequire 'vendor/autoload.php';\n\nuse GuzzleHttp\Client;\n\nfunction fetchImages($url) {\n    $client = new Client();\n    $response = $client->request('GET', $url);\n    $html = (string)$response->getBody();\n\n    // Load the HTML into a DOMDocument object\n    libxml_use_internal_errors(true); // Suppress warnings for malformed HTML\n    $dom = new DOMDocument();\n    $dom->loadHTML($html);\n    libxml_clear_errors();\n\n    $xpath = new DOMXPath($dom);\n    $images = $xpath->query('//img');\n\n    $filteredImages = [];\n\n    foreach ($images as $image) {\n        $src = $image->getAttribute('src');\n        if (!$src) continue;\n\n        // Create an absolute URL\n        if (strpos($src, 'http') !== 0) {\n            $src = $url . DIRECTORY_SEPARATOR . ltrim($src, '/');\n        }\n\n        // Fetch image dimensions\n        list($width, $height) = @getimagesize($src);\n        \n        // Filter images based on size, adjust the values as needed\n        if ($width > 100 && $height > 100) {\n            $filteredImages[] = ['url' => $src, 'width' => $width, 'height' => $height];\n        }\n    }\n\n    return $filteredImages;\n}\n\n$url = "http://example.com"; // Replace with the URL of interest\n$images = fetchImages($url);\n\necho json_encode($images, JSON_PRETTY_PRINT);