function doSomething()\n{\n    //..code here..\n    \n    $startTime = microtime(true); // Start timing\n    $maxExecutionTime = 5; // Maximum execution time in seconds\n\n    function1($startTime, $maxExecutionTime);\n\n    //.. some code here..\n}\n\nfunction function1($startTime, $maxExecutionTime)\n{\n    while (someCondition) {\n        // Your code logic here\n        \n        // Check if the time limit has been reached\n        if ((microtime(true) - $startTime) > $maxExecutionTime) {\n            break; // Exit the loop or function if time limit is exceeded\n        }\n    }\n}