<?php\n\n$html = '<TEXTFORMAT LEADING=\'2\'><P ALIGN=\'LEFT\'><FONT FACE=\'Verdana\' style="font-size:10\' COLOR=\'#0B333C\'>My name\'s Mark</FONT></P></TEXTFORMAT>';\n\n// This pattern matches single quotes inside HTML attribute values\n$pattern = '/(?<=[:\[\(\{=])\'|\'(?=[\]\)\},>])/';\n\n// Replace matched single quotes with double quotes\n$safeHtml = preg_replace($pattern, '"', $html);\n\necho htmlspecialchars($safeHtml, ENT_QUOTES, 'UTF-8');\n\n?>