class My_Decorator_LabelWrapper extends Zend_Form_Decorator_Abstract\n{\n    public function render($content)\n    {\n        $element = $this->getElement();\n        \n        if (!$element instanceof Zend_Form_Element) {\n            return $content;\n        }\n        \n        $labelOptions = $element->getDecorator('Label')->getOptions();\n        $label = $this->getMarkupElementLabel($element);\n        \n        // Get the input tag that is currently rendered\n        $inputTag = $content;\n\n        // Render the label with the input inside it\n        $markup = sprintf(\n            '<label for="%s">%s %s</label>',\n            $element->getId(),\n            $label,\n            $inputTag\n        );\n\n        return $markup;\n    }\n}