$ch1 = curl_init();\ncurl_setopt($ch1, CURLOPT_URL,$purl);\ncurl_setopt($ch1, CURLOPT_POST, true);\ncurl_setopt($ch1, CURLOPT_POSTFIELDS, $post_fields);\ncurl_setopt($ch1, CURLOPT_RETURNTRANSFER,true);\ncurl_setopt($ch1, CURLOPT_FOLLOWLOCATION, 0); // do not follow the redirection to capture headers\ncurl_setopt($ch1, CURLOPT_HEADER, true ); // include header in output\ncurl_setopt($ch1, CURLOPT_COOKIE,'cookie.txt');\ncurl_setopt($ch1, CURLOPT_COOKIEJAR,'cookie.txt');\ncurl_setopt($ch1, CURLOPT_COOKIEFILE,'cookie.txt');\n$result2= curl_exec ($ch1);\ncurl_close ($ch1);\n\n// Extract the Location from the response headers\n$header_size = curl_getinfo($ch1, CURLINFO_HEADER_SIZE);\n$headers = substr($result2, 0, $header_size);\npreg_match('/Location: (.*)\n/', $headers, $matches);\nif (isset($matches[1])) {\n    $topicUrl = $matches[1];\n    // Extract t parameter using parse_url and parse_str\n    $parsedUrl = parse_url($topicUrl);\n    parse_str($parsedUrl['query'], $queryParams);\n    if (isset($queryParams['t'])) {\n        $tValue = $queryParams['t'];\n        echo "The topic ID 't' is: $tValue";\n        // Store this tValue in your SQL table linked to the question\n    }\n}