// Step 1: Check if user exists\n$usernameCheckQuery = "SELECT 1 FROM USERS WHERE username=?";\nif ($usernameStmt = $con->prepare($usernameCheckQuery)) {\n    $usernameStmt->bind_param("s", $subcat); // Assuming $subcat holds the username to check\n    $usernameStmt->execute();\n    $result = $usernameStmt->get_result();\n\n    // Step 2: Insert if user does not exist\n    if ($result->num_rows == 0) { // Check if no rows returned, meaning user doesn't exist\n        $insertUserQuery = "INSERT INTO USERS (username) VALUES (?)";\n        if ($insertStmt = $con->prepare($insertUserQuery)) {\n            $insertStmt->bind_param("s", $subcat);\n            $insertStmt->execute();\n            $insertStmt->close();\n        }\n    }\n    $usernameStmt->close();\n\n    // Step 3: Update AUCTIONS table\n    $alterQuery = "UPDATE AUCTIONS SET subcat= ? WHERE article_no= ?";\n    if ($altRecord = $con->prepare($alterQuery)) {\n        $altRecord->bind_param("ss", $subcat, $pk);\n        $altRecord->execute();\n        $altRecord->close();\n        echo "true";\n    }\n} else {\n    echo "Error in preparing statement: " . $con->error;\n}