// Assuming $pdo is an instance of PDO already configured\n\nif (isset($_POST['pesquisar'])) {\n    $queryStr = 'SELECT * FROM tbl_clientes WHERE 1=1';\n    $params = [];\n\n    if (!empty($_POST['nome'])) {\n        $queryStr .= ' AND nome LIKE :nome';\n        $params[':nome'] = '%' . $_POST['nome'] . '%';\n    }\n\n    // Handle other fields similarly if needed\n\n    $stmt = $pdo->prepare($queryStr);\n    $stmt->execute($params);\n    $results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\n    // Process results...\n}