function convertImage($sourceFile) {\n    // Check if file exists\n    if (!file_exists($sourceFile)) {\n        throw new Exception("Source file does not exist.");\n    }\n\n    // Load image resource\n    $image = imagecreatefromjpeg($sourceFile);\n    if (!$image) {\n        throw new Exception("Failed to load image.");\n    }\n\n    // Save as PNG\n    $pngFile = pathinfo($sourceFile, PATHINFO_FILENAME) . '.png';\n    imagepng($image, $pngFile);\n\n    // Save as GIF\n    $gifFile = pathinfo($sourceFile, PATHINFO_FILENAME) . '.gif';\n    imagegif($image, $gifFile);\n\n    // Save as BMP\n    $bmpFile = pathinfo($sourceFile, PATHINFO_FILENAME) . '.bmp';\n    imagewbmp($image, $bmpFile);\n\n    // Free up memory\n    imagedestroy($image);\n}\n\n// Usage\ntry {\n    convertImage('path/to/your/image.jpg');\n} catch (Exception $e) {\n    echo 'Error: ' . $e->getMessage();\n}