$lastGroupName = null;\n\nwhile ($data = $result->fetch_assoc()) {\n    $currentGroupName = $data['product_groups.name'];\n    \n    if ($lastGroupName !== $currentGroupName) {\n        echo "Group: " . htmlspecialchars($currentGroupName) . "<br>";\n        $lastGroupName = $currentGroupName;\n    }\n    \n    echo "Product ID: " . htmlspecialchars($data['products.id']) . ", ";\n    echo "Product Name: " . htmlspecialchars($data['products.name']) . "<br><br>";\n}