<?php\n\n// Assuming $xmlInput is the XML data sent via POST\n$xmlInput = isset($_POST['xml']) ? $_POST['xml'] : '';\n\ntry {\n    // Load the XML from the input\n    $sxe = new SimpleXMLElement($xmlInput);\n    \n    // Extract Criterions\n    $criterions = $sxe->Criterions;\n    \n    // Initialize criteria variables\n    $orderNumber = (string) $criterions->OrderNumber;\n    $startDate = (string) $criterions->StartDate;\n    $endDate = (string) $criterions->EndDate;\n\n    // Output the extracted fields for verification or processing\n    echo "Order Number: " . htmlspecialchars($orderNumber) . "\n";\n    echo "Start Date: " . htmlspecialchars($startDate) . "\n";\n    echo "End Date: " . htmlspecialchars($endDate) . "\n";\n\n    // Here you would use $startDate and $endDate to query your database\n} catch (Exception $e) {\n    echo 'Error parsing XML: ',  $e->getMessage(), "\n";\n}\n\n?>