function generateKeywordsForCategory($category_id, $mysqli) {\n    // Fetch text data from the database\n    $stmt = $mysqli->prepare("SELECT text_column FROM texts WHERE category_id = ?");\n    $stmt->bind_param('i', $category_id);\n    $stmt->execute();\n    $texts = $stmt->get_result()->fetch_all(MYSQLI_ASSOC);\n\n    // Combine texts into one large string and split into words\n    $combined_text = '';\n    foreach ($texts as $text) {\n        $combined_text .= ' ' . strip_tags($text['text_column']);\n    }\n\n    $words = preg_split('/[\s,]+/', strtolower($combined_text), -1, PREG_SPLIT_NO_EMPTY);\n\n    // Count word frequencies\n    $word_counts = array_count_values($words);\n    \n    // Remove common stop words (you can expand this list)\n    $stop_words = ['the', 'and', 'to', 'of', 'in', 'a', 'on', 'is', 'for'];\n    $filtered_word_counts = array_diff_key($word_counts, array_flip($stop_words));\n\n    // Sort by frequency and get top 10\n    arsort($filtered_word_counts);\n    $top_words = array_slice(array_keys($filtered_word_counts), 0, 10);\n\n    return implode(', ', $top_words);\n}