<?php\n// Assuming $pdo is a PDO database connection object\n\n$query = "\n    SELECT \n        DATE_FORMAT(FROM_UNIXTIME(`date`), '%M %Y') AS archive_month,\n        COUNT(*) as post_count\n    FROM \n        posts\n    GROUP BY \n        archive_month\n    ORDER BY \n        `date` DESC;\n";\n\n$stmt = $pdo->query($query);\n$archiveLinks = [];\n\nwhile ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n    $archiveLinks[] = htmlspecialchars($row['archive_month'], ENT_QUOTES, 'UTF-8');\n}\n\n// Display the archive links\nforeach ($archiveLinks as $link) {\n    echo "<li>$link</li>";\n}\n?>