// Assuming $finalPath is the path where the file was moved\n$finalPath = 'C:/path/to/final/location/yourfile.ext';\n\n// Set default user and group (replace with actual names as needed)\n$defaultUser = 'DOMAIN\\YourUserName';\n$defaultGroup = 'DOMAIN\\YourGroupName';\n\n// Set permissions for file\n// 0644 means read/write for owner, read for group and others\nchmod($finalPath, 0644);\n\n// You can also use shell_exec to run icacls or cacls to set specific NTFS permissions\nshell_exec('cacls ' . escapeshellarg($finalPath) . ' /G ' . escapeshellarg($defaultGroup) . ':R');\n\n// If you need more detailed control, consider using icacls:\n// shell_exec('icacls ' . escapeshellarg($finalPath) . ' /grant ' . escapeshellarg($defaultGroup) . ':(OI)(CI)R');