<?php\n   defined('BASEPATH') OR exit('No direct script access allowed');\n\n   class MY_Exceptions extends CI_Exceptions {\n\n       public function __construct() {\n           parent::__construct();\n           \n           // Set a custom error handler\n           set_error_handler(array($this, 'handle_php_errors'));\n       }\n\n       public function handle_php_errors($severity, $message, $filepath, $line)\n       {\n           if (!(error_reporting() & $severity)) return;\n           \n           $email = \Config\Services::email();\n           $email->setTo('your-email@example.com');\n           $email->setFrom('noreply@yoursite.com', 'Your Site Error Logs');\n           $email->setSubject('PHP Script Error [' . date("Y-m-d H:i:s") . ']');\n\n           // Create the error message\n           $error_message = "Severity: {$severity}  --> {$message} {$filepath} {$line}";\n\n           $email->setMessage($error_message);\n           \n           if (!$email->send()) {\n               print_r($email->printDebugger());\n           }\n       }\n   }\n   \n   ?>