<!-- View file: admin/index.php -->\n   <h1>Admin Posts</h1>\n   <?php foreach ($posts as $post): ?>\n       <div>\n           <h2><?php echo htmlspecialchars($post->title, ENT_QUOTES, 'UTF-8'); ?></h2>\n           <p><?php echo htmlspecialchars($post->content, ENT_QUOTES, 'UTF-8'); ?></p>\n           <!-- Links to edit and delete posts -->\n           <a href="<?php echo site_url('admin/edit/' . $post->id); ?>">Edit</a>\n           <a href="<?php echo site_url('admin/delete/' . $post->id); ?>">Delete</a>\n       </div>\n   <?php endforeach; ?>