// Content Type Headers\nheader('Content-Type: text/html; charset=UTF-8');\nheader('Content-Type: application/json');\nheader('Content-Type: image/png');\n\n// Cache Control Headers\nheader('Cache-Control: no-cache, no-store, must-revalidate'); // HTTP 1.1.\nheader('Pragma: no-cache'); // HTTP 1.0.\nheader('Expires: 0'); // Proxies.\n\n// Location Header (used for redirection)\nheader('Location: http://www.example.com/');\n\n// Content Disposition Header (used for downloading files)\nheader('Content-Disposition: attachment; filename="example.pdf"');\n\n// Status Codes\nheader("HTTP/1.1 404 Not Found");\nheader("HTTP/1.1 200 OK");\n\n// Cookies\nheader('Set-Cookie: name=value; expires=Wed, 09 Jun 2023 10:18:14 GMT');\n\n// Security Headers (to prevent XSS and clickjacking)\nheader("X-XSS-Protection: 1; mode=block");\nheader("X-Frame-Options: SAMEORIGIN");\n\n// CORS Header\nheader('Access-Control-Allow-Origin: *');