<?php\n\nclass WebPage {\n    private $title;\n    private $headContent = '';\n    private $bodyContent = '';\n\n    public function __construct($title) {\n        $this->title = $title;\n    }\n\n    public function addHeadContent($content) {\n        $this->headContent .= "\n" . $content;\n    }\n\n    public function addBodyContent($content) {\n        $this->bodyContent .= "\n" . $content;\n    }\n\n    public function display() {\n        echo '<!DOCTYPE html>';\n        echo '<html lang="en">';\n        echo '<head>';\n        echo "<title>" . htmlspecialchars($this->title) . "</title>";\n        echo $this->headContent;\n        echo '</head>';\n        echo '<body>';\n        echo $this->bodyContent;\n        echo '</body>';\n        echo '</html>';\n    }\n}\n\n// Example usage:\n$page = new WebPage('My Fun Website');\n$page->addHeadContent('<meta charset="UTF-8">');\n$page->addBodyContent('<h1>Welcome to My Fun Website</h1>');\n$page->addBodyContent('<p>This is a paragraph on my website.</p>');\n$page->display();\n\n?>