class plzComplicateMyLife {\n    public $vehicle;\n    public $kindVehicle;\n    private $dynamicFunction;\n\n    public function __construct($vehicle, $kindVehicle) {\n        $this->kindVehicle = $kindVehicle;\n        $this->vehicle = $vehicle;\n\n        switch ($kindVehicle) {\n            case 'cycle':\n                $this->dynamicFunction = [$this, 'isACycle'];\n                break;\n            case 'car':\n                $this->dynamicFunction = [$this, 'isACar'];\n                break;\n        }\n    }\n\n    public function isACycle($vehicle) {\n        echo 'im a cycle, model: ' . $vehicle . '<br />';\n    }\n\n    public function isACar($vehicle) {\n        echo 'im a car, model: ' . $vehicle . '<br />';\n    }\n\n    public function printKind() {\n        call_user_func($this->dynamicFunction, $this->vehicle);\n    }\n}\n\n$maserati = new plzComplicateMyLife('maserati4', 'car');\n$ducati = new plzComplicateMyLife('ducati959', 'cycle');\n\n$maserati->printKind(); // Output: im a car, model: maserati4\n$ducati->printKind();   // Output: im a cycle, model: ducati959