class Movie {\n         private $conn;\n\n         public function __construct($dbConnection) {\n             $this->conn = $dbConnection;\n         }\n\n         public function fetchMovieById($id) {\n             $stmt = $this->conn->prepare("SELECT * FROM movies WHERE id=?");\n             $stmt->bind_param("i", $id);\n             $stmt->execute();\n             return $stmt->get_result();\n         }\n     }