function performFunction($value) {\n    // Perform any necessary transformations or checks here\n    return strtoupper($value); // Example transformation: convert to uppercase\n}\n\nif ($stmt = $mysqli->prepare("SELECT * FROM sample WHERE t2 LIKE ?")) {\n    $tt2 = '%';\n    $stmt->bind_param("s", $tt2);\n    $stmt->execute();\n\n    $meta = $stmt->result_metadata();\n    while ($field = $meta->fetch_field()) {\n        $params[] = &$row[$field->name];\n    }\n\n    call_user_func_array(array($stmt, 'bind_result'), $params);\n\n    $result = [];\n    while ($stmt->fetch()) {\n        $c = []; // Initialize array for each row\n        foreach ($row as $key => $val) {\n            $c[$key] = performFunction($val);\n        }\n        $result[] = $c;\n    }\n\n    $stmt->close();\n}\n$mysqli->close();\n\nreturn $result; // Return the result instead of printing it