class Blog_model extends CI_Model {\n       public function get_posts() {\n           return $this->db->get('posts')->result();\n       }\n\n       public function add_post($post_data) {\n           return $this->db->insert('posts', $post_data);\n       }\n\n       public function update_post($id, $post_data) {\n           $this->db->where('id', $id);\n           return $this->db->update('posts', $post_data);\n       }\n\n       public function delete_post($id) {\n           return $this->db->delete('posts', array('id' => $id));\n       }\n   }