function getFirstWords($string, $numWords) {\n    // Split the string into an array of words\n    $wordsArray = str_word_count($string, 1);\n    \n    // Get the first 'n' words from the array\n    $firstWords = array_slice($wordsArray, 0, $numWords);\n    \n    // Join the words back into a single string with spaces\n    return implode(' ', $firstWords);\n}\n\n// Example usage\n$text = "Here is your block of text. It should contain more than fifty words so that you can properly test this PHP function to ensure it extracts the first fifty words without cutting off any word in the middle.";\necho getFirstWords($text, 50);