use Symfony\Component\HttpFoundation\Session\Storage\NativeFileSessionStorage;\nuse Symfony\Component\HttpFoundation\Session\Session;\n\n// Create a session storage instance.\n$storage = new NativeFileSessionStorage();\n\n// You can also use other storage methods like Memcached, Redis, etc.\n// $storage = new MemcachedSessionStorage(...);\n\n// Create the Session object.\n$session = new Session($storage);\n\n// Start the session.\n$session->start();\n\n// Set a simple value.\n$session->set('foo', 'bar');\n\n// Get a value.\n$value = $session->get('foo');\n\n// Handle flash data (it will only last for one request).\n$session->getFlashBag()->add('notice', 'This is a notice.');\n\n// Handling session_regenerate_id equivalent.\n$session->migrate(true);\n\n// End the session and save all changes.\n$session->save();