<?php\n// Define the path to rrdtool executable\n$rrd_path = '/usr/bin/rrdtool';\n\n// Create an RRD file with one DS (Data Source) and one RRA (Round Robin Archive)\n$create_command = $rrd_path . " create stats.rrd --step 300 DS:loadavg:AVERAGE:600:U:U RRA:AVERAGE:0.5:1:24";\nexec($create_command, $output, $return_var);\n\nif ($return_var !== 0) {\n    echo "Failed to create RRD file:\n" . implode("\n", $output);\n} else {\n    echo "RRD file created successfully.\n";\n\n    // Update the RRD with some data (assuming load avg is 0.2)\n    $update_command = $rrd_path . " update stats.rrd N:0.2";\n    exec($update_command, $output, $return_var);\n\n    if ($return_var !== 0) {\n        echo "Failed to update RRD file:\n" . implode("\n", $output);\n    } else {\n        echo "RRD file updated successfully.\n";\n    }\n}\n?>