function formatNumber($number) {\n    // Convert the number to a string\n    $numStr = strval($number);\n    \n    // Pad the number with leading zeros to ensure it has at least 7 digits\n    $paddedNum = str_pad($numStr, 7, '0', STR_PAD_LEFT);\n    \n    // Split the padded number into the desired format\n    return substr($paddedNum, 0, 3) . '/' . substr($paddedNum, 3, 3) . '/' . substr($paddedNum, 6);\n}\n\n// Examples\necho formatNumber(12345);     // Outputs: 000/012/345\necho "\n";\necho formatNumber(9876543);   // Outputs: 009/876/543