<?php\n$file = '/var/www/htdocs/images/file.jpg';\n$mime = 'image/jpeg';\n\nif (file_exists($file)) {\n    header('Last-Modified: ' . gmdate('D, d M Y H:i:s T', filemtime($file)));\n    header('Content-Type: ' . $mime);\n    header('Content-Length: ' . filesize($file));\n    header('Etag: ' . md5_file($file)); // Use md5_file to hash the file content\n    readfile($file);  // Output the image file directly\n} else {\n    header("HTTP/1.0 404 Not Found");\n    echo "File not found.";\n}\n?>