class Session {\n       private function __construct() {\n           session_start();\n       }\n\n       public static function start() {\n           if(session_status() == PHP_SESSION_NONE) {\n               new self();\n           }\n       }\n\n       public static function set($key, $value) {\n           $_SESSION[$key] = $value;\n       }\n\n       public static function get($key) {\n           return $_SESSION[$key] ?? null;\n       }\n   }