require_once('path/to/simplepie/autoloader.php');\n\n// Create a new instance of the SimplePie class\n$feed = new SimplePie();\n\n$feedUrl = 'http://example.com/feed';\n\n// Set the feed URL and initialize the object with it\n$feed->set_feed_url($feedUrl);\n$feed->init();\n$feed->handle_content_type();\n\n// Check if there are any items in this feed.\nif ($feed->get_items()) {\n    echo "Found a valid feed!";\n} else {\n    echo "Could not find a valid feed.";\n}\n\n// Loop through each item\nforeach ($feed->get_items() as $item) {\n    // Output the title of the item\n    echo '<h3><a href="' . esc_url($item->get_permalink()) . '">' . htmlspecialchars($item->get_title()) . '</a></h3>';\n}