$query = "SELECT tabx.*, taby.* FROM tabx JOIN taby ON ... WHERE ...";\n$result = mysqli_query($connection, $query);\n\nif ($result) {\n    while ($row = mysqli_fetch_assoc($result)) {\n        foreach ($row as $key => $value) {\n            if (strpos($key, 'tabx_') === 0) {\n                echo "Column $key from tabx: $value\n";\n            } elseif (strpos($key, 'taby_') === 0) {\n                echo "Column $key from taby: $value\n";\n            }\n        }\n    }\n}