function get_book($id) {\n    // Check if the book ID exists in the database\n    $query = $this->db->get_where('books', array('id' => $id));\n\n    if ($query->num_rows() == 0) {\n        // No book found with this ID, show a 404 error\n        show_404();\n    } else {\n        // Proceed to display the book details\n        $data['book'] = $query->row();\n        $this->load->view('book_details', $data);\n    }\n}