function getAllSessions(PDO $pdo) {\n       $stmt = $pdo->query("SELECT id, data FROM php_sessions");\n       while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n           $sessionData = unserialize($row['data']);\n           // Assume $_SESSION['user'] is set in your session\n           if (isset($sessionData['user'])) {\n               echo "Session ID: {$row['id']} - User Data: " . print_r($sessionData['user'], true) . "\n";\n           }\n       }\n   }\n\n   getAllSessions($pdo);