<?php\nsession_name('MySessID');\nsession_start();\n// Set any session variables needed\n\n// Update or retrieve data in the session as required\n$_SESSION['some_key'] = 'some_value';\n\n// Close the session file to release the lock\nsession_write_close();\n\n// Now make the cURL request\n$ch = curl_init('http://example.com/script2.php');\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n$response = curl_exec($ch);\ncurl_close($ch);\n\necho $response;\n?>