<?php\n\nclass LoadBalancer {\n    private $servers;\n    private $currentIndex;\n\n    public function __construct($servers) {\n        $this->servers = $servers;\n        $this->currentIndex = 0;\n    }\n\n    public function getNextServer() {\n        // Get the current server and then increment the index\n        $server = $this->servers[$this->currentIndex];\n        $this->currentIndex++;\n\n        // Reset index to 0 if it exceeds the number of servers\n        if ($this->currentIndex >= count($this->servers)) {\n            $this->currentIndex = 0;\n        }\n\n        return $server;\n    }\n}\n\n// Define your server URLs or IPs\n$servers = [\n    'http://processing1.example.com',\n    'http://processing2.example.com',\n    'http://processing3.example.com'\n];\n\n$loadBalancer = new LoadBalancer($servers);\n\n// Example usage - getting the next server to send a request to\n$nextServer = $loadBalancer->getNextServer();\necho "Next server is: " . $nextServer;\n\n?>