<?php\n\nfunction cleanInput($filename) {\n    // Read the content of the file\n    $content = file_get_contents($filename);\n    \n    // Remove all ASCII control characters except tabs, newlines, and carriage returns\n    $cleanContent = preg_replace('/[\x00-\x08\x0B\x0C\x0E-\x1F]/', '', $content);\n    \n    return $cleanContent;\n}\n\n$filename = 'test.html';\n$cleanFilename = 'clean_test.html';\n\n// Clean the input file\nfile_put_contents($cleanFilename, cleanInput($filename));\n\n$doc = new DOMDocument();\n@$doc->loadHTMLFile($cleanFilename); // Use @ to suppress warnings\n\n// Further processing...\n?>