<?php\n// Create a connection\n$mysqli = new mysqli("host", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Prepare and bind\n$stmt = $mysqli->prepare("UPDATE offtopic SET next = ? WHERE id = ?");\n$stmt->bind_param("si", $insert, $id);\n\n// Set parameters and execute\n$insert = 'your_value_here'; // Replace with actual value\n$id = 1; // Replace with actual ID\n\nif ($stmt->execute()) {\n    echo "Record updated successfully";\n} else {\n    echo "Error updating record: " . $mysqli->error;\n}\n\n// Close connections\n$stmt->close();\n$mysqli->close();\n?>